(function($){$.fn.placeholder=function(options){var settings={defaultValue:'',placeholderProperty:'placeholder'};if(options){$.extend(settings,options)}if(settings.defaultValue==''){var defaultVal=this.attr(settings.placeholderProperty)}else{var defaultVal=settings.defaultValue}$(this).val(defaultVal);this.click(function(){if($(this).val()==defaultVal){$(this).val("")}});this.blur(function(){if($(this).val()==''){$(this).val(defaultVal)}})}})(jQuery)
